select.execute:
    var random = "_" + Math.random();
    var url = "http://finance.yahoo.com/quote/" + symbol +"/profile";
    var realtimeQuery = y.query(
	"select * from html where url=@url and " + 
	"xpath='//*[@id=\"quote-header-info\"]/div[2]/div[1]/div' " +
	"limit 4",
	{url:url});
    var quote = <quote symbol={symbol}></quote>;
    var results = realtimeQuery.results;
    var elements = results.*.length(); 
    if (elements != 0) {
	quote.appendChild(<quote>{results.div.span[0].toString()}</quote>);
	quote.appendChild(<change>{results.div.span[1].toString()}</change>);
	quote.appendChild(<notice>{results.div.div.span.toString()}</notice>);
    }
    response.object = quote
